function orderRange(){let e=document.querySelector(".order");if(e){let t=document.querySelector(".order__input-range"),r=document.querySelector(".order__range-output output");r.textContent=t.value,t.addEventListener("input",e=>{r.textContent=e.target.value})}}function testimonialsSwiper(){new Swiper(".swiper",{loop:!0,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},scrollbar:{el:".swiper-scrollbar"}})}window.addEventListener("load",()=>{orderRange(),testimonialsSwiper()});